sp_widget.client_script:
function mcIncidentFeedbackController(mcDemoDataService) {
  var vm = this;

  vm.$onInit = function () {
    vm.incident = (vm.options ? vm.options.incident : '');
    if (!vm.incident) {
      vm.dataError = 'Incident identifier is not provided';
      return;
    }

    vm.submit = submit;
  };

  function submit() {
    vm.messageError = '';
    vm.ratingError = '';
    var error = false;
    if (!vm.rating) {
      vm.ratingError = 'Please select rating value';
      error = true;
    }

    if (!vm.message) {
      vm.messageError = 'Please populate message';
      error = true;
    }

    if (error) {
      return;
    }

    return mcDemoDataService.createRecord('x_86566_k18_sp_incident_feedback',
      {
        incident: vm.incident,
        rating: vm.rating,
        message: vm.message})
      .then(function () {
        alert('Your feedback has been added');
      });
  }
}
